{
    "$schema": "https://schema.management.azure.com/providers/Microsoft.Logic/schemas/2016-06-01/workflowdefinition.json#",
    "actions": {
        "Send_HTTP_Request_to_Chrome": {
            "inputs": {
                "headers": {
                    "Content-Type": "application/json"
                },
                "method": "POST",
                "uri": "https://your-chrome-endpoint-url",
                "body": {
                    // Content that should be sent to Chrome
                    "key1": "value1",
                    "key2": "value2"
                }
            },
            "runAfter": {},
            "type": "Http"
        }
    },
    "contentVersion": "1.0.0.0",
    "outputs": {},
    "triggers": {
        "When_an_HTTP_request_is_received": {
            "inputs": {
                "schema": {
                    "properties": {
                        "key1": {
                            "type": "string"
                        },
                        "key2": {
                            "type": "string"
                        }
                    },
                    "type": "object"
                }
            },
            "recurrence": null,
            "type": "Request"
        }
    }
}
